mapper.insert:

insert into tra_touris_catel_rel (order_id, tourist_id, food_id, 
  order_date, order_num, status_id
  )
values (#{orderId,jdbcType=INTEGER}, #{touristId,jdbcType=INTEGER}, #{foodId,jdbcType=INTEGER}, 
  #{orderDate,jdbcType=TIMESTAMP}, #{orderNum,jdbcType=INTEGER}, #{statusId,jdbcType=INTEGER}
  )

mapper.select:
  			select 
				c.order_id,
				c.tourist_id,
				t.tourist_nickname,
				c.food_id,
				f.food_name,
				c.order_date,
				c.order_num,
				c.status_id
			from tra_touris_catel_rel c ,tra_tourist t , tra_foods f where c.tourist_id = t.tourist_id and f.food_id=c.food_id and c.food_id in (
			  select food_id from tra_foods where food_id in (
			   select food_id from tra_cate_food_rel where cate_id in (
			    select cate_id from tra_cate where cate_id in (
			     select cate_id from tra_cate_group_rel where cgp_id in (
			       select cgp_id from tra_cate_group where cgp_id = #{cgpId,jdbcType=INTEGER}
			     )
			    )
			   )
			  )
			 )
